import type { Express, Request, Response, NextFunction } from "express";
import { storage } from "./storage";
import { insertCashReceiptSchema, insertCashDisbursementSchema, insertChartOfAccountsSchema, insertCompanySchema } from "@shared/schema";
import * as XLSX from "xlsx";

// Middleware to check authentication
function requireAuth(req: Request, res: Response, next: NextFunction) {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ message: "Unauthorized" });
  }
  next();
}

// Middleware to get user's company ID
async function getCompanyId(req: Request): Promise<number | null> {
  const user = req.user as any;
  if (!user) return null;

  const dbUser = await storage.getUser(user.id);
  return dbUser?.companyId || null;
}

export async function registerRoutes(app: Express): Promise<void> {
  // Auth routes
  app.get("/api/auth/user", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Unauthorized" });
    }
    const user = await storage.getUser((req.user as any).id);
    res.json(user);
  });

  // Company routes
  app.get("/api/company", requireAuth, async (req, res) => {
    try {
      const companyId = await getCompanyId(req);
      if (!companyId) {
        return res.status(404).json({ message: "Company not found" });
      }
      const company = await storage.getCompany(companyId);
      res.json(company);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch company" });
    }
  });

  app.patch("/api/company", requireAuth, async (req, res) => {
    try {
      const companyId = await getCompanyId(req);
      if (!companyId) {
        return res.status(404).json({ message: "Company not found" });
      }
      const validated = insertCompanySchema.partial().parse(req.body);
      const updated = await storage.updateCompany(companyId, validated);
      res.json(updated);
    } catch (error) {
      res.status(500).json({ message: "Failed to update company" });
    }
  });

  // Chart of Accounts routes
  app.get("/api/chart-of-accounts", requireAuth, async (req, res) => {
    try {
      const companyId = await getCompanyId(req);
      if (!companyId) {
        return res.status(404).json({ message: "Company not found" });
      }
      const accounts = await storage.getChartOfAccounts(companyId);
      res.json(accounts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch accounts" });
    }
  });

  app.post("/api/chart-of-accounts", requireAuth, async (req, res) => {
    try {
      const companyId = await getCompanyId(req);
      if (!companyId) {
        return res.status(404).json({ message: "Company not found" });
      }
      const validated = insertChartOfAccountsSchema.parse({ ...req.body, companyId });
      const account = await storage.createChartOfAccount(validated);
      res.status(201).json(account);
    } catch (error) {
      res.status(500).json({ message: "Failed to create account" });
    }
  });

  app.patch("/api/chart-of-accounts/:id", requireAuth, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const validated = insertChartOfAccountsSchema.partial().parse(req.body);
      const updated = await storage.updateChartOfAccount(id, validated);
      res.json(updated);
    } catch (error) {
      res.status(500).json({ message: "Failed to update account" });
    }
  });

  app.delete("/api/chart-of-accounts/:id", requireAuth, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteChartOfAccount(id);
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete account" });
    }
  });

  // Cash Receipts routes
  app.get("/api/cash-receipts", requireAuth, async (req, res) => {
    try {
      const companyId = await getCompanyId(req);
      if (!companyId) {
        return res.status(404).json({ message: "Company not found" });
      }
