  Smartphone,
} from "lucide-react";
import type { Company, Subscription as SubscriptionType } from "@shared/schema";

export default function Subscription() {
  const { toast } = useToast();

  const { data: company, isLoading: companyLoading } = useQuery<Company>({
    queryKey: ["/api/company"],
  });

  const { data: payments } = useQuery<SubscriptionType[]>({
    queryKey: ["/api/subscriptions"],
  });

  const gcashMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/subscriptions/gcash");
      return response;
    },
    onSuccess: (data: any) => {
      if (data?.checkoutUrl) {
        window.location.href = data.checkoutUrl;
      } else {
        toast({
          title: "Payment initiated",
          description: "Please complete the payment in GCash.",
        });
      }
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to initiate payment. Please try again.",
        variant: "destructive",
      });
    },
  });

  const stripeMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/subscriptions/stripe");
      return response;
    },
    onSuccess: (data: any) => {
      if (data?.checkoutUrl) {
        window.location.href = data.checkoutUrl;
      }
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to initiate payment. Please try again.",
        variant: "destructive",
      });
    },
  });

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "active":
        return <Badge className="bg-primary/10 text-primary"><CheckCircle className="mr-1 h-3 w-3" />Active</Badge>;
      case "trial":
        return <Badge variant="secondary"><Crown className="mr-1 h-3 w-3" />Trial</Badge>;
      default:
        return <Badge variant="destructive"><AlertCircle className="mr-1 h-3 w-3" />Expired</Badge>;
    }
  };

  const annualPrice = 2999;

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-semibold">Subscription</h1>
        <p className="text-muted-foreground">Manage your LLAS subscription</p>
      </div>

      <div className="grid gap-6 lg:grid-cols-2">
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between gap-2">
              <CardTitle className="flex items-center gap-2">
                <Crown className="h-5 w-5 text-primary" />
                Current Plan
              </CardTitle>
              {company && getStatusBadge(company.subscriptionStatus)}
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="rounded-lg bg-muted p-4">
              <h3 className="text-xl font-semibold">LLAS Annual Plan</h3>
              <p className="text-3xl font-bold text-primary mt-2">
                {formatPHP(annualPrice)}
                <span className="text-sm font-normal text-muted-foreground">/year</span>
              </p>
            </div>

            {company && (
              <div className="space-y-3">
                <div className="flex items-center gap-3">
                  <Calendar className="h-4 w-4 text-muted-foreground" />
                  <div>
                    <p className="text-sm font-medium">Subscription Period</p>
                    <p className="text-sm text-muted-foreground">
                      {company.subscriptionStartDate && company.subscriptionEndDate
                        ? `${formatDate(company.subscriptionStartDate)} - ${formatDate(company.subscriptionEndDate)}`
                        : "No active subscription"}
                    </p>
                  </div>
                </div>
              </div>
            )}

            <Separator />

            <div className="space-y-2">
              <h4 className="font-medium">What's Included:</h4>
              <ul className="space-y-2">
                <li className="flex items-center gap-2 text-sm">
                  <CheckCircle className="h-4 w-4 text-primary" />
                  Unlimited voucher entries
                </li>
                <li className="flex items-center gap-2 text-sm">
                  <CheckCircle className="h-4 w-4 text-primary" />
                  Complete Excel workbook export
                </li>
                <li className="flex items-center gap-2 text-sm">
                  <CheckCircle className="h-4 w-4 text-primary" />
                  All 7 financial sheets
                </li>
                <li className="flex items-center gap-2 text-sm">
                  <CheckCircle className="h-4 w-4 text-primary" />
                  Automatic VAT calculations
                </li>
                <li className="flex items-center gap-2 text-sm">
                  <CheckCircle className="h-4 w-4 text-primary" />
                  Multi-user access
                </li>
                <li className="flex items-center gap-2 text-sm">
                  <CheckCircle className="h-4 w-4 text-primary" />
                  BIR-compliant reports
                </li>
              </ul>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Payment Options</CardTitle>
            <CardDescription>
              Choose your preferred payment method
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <Button
              className="w-full justify-start gap-3 h-auto py-4"
              variant="outline"
              onClick={() => gcashMutation.mutate()}
              disabled={gcashMutation.isPending}
              data-testid="button-pay-gcash"
            >
              <div className="flex h-10 w-10 items-center justify-center rounded-full bg-blue-500">
                <Smartphone className="h-5 w-5 text-white" />
              </div>
              <div className="text-left">
                <p className="font-medium">Pay with GCash</p>
                <p className="text-sm text-muted-foreground">
                  Quick and easy mobile payment
                </p>
              </div>
            </Button>

            <Button
              className="w-full justify-start gap-3 h-auto py-4"
              variant="outline"
              onClick={() => stripeMutation.mutate()}
              disabled={stripeMutation.isPending}
              data-testid="button-pay-stripe"
            >
              <div className="flex h-10 w-10 items-center justify-center rounded-full bg-purple-500">
                <CreditCard className="h-5 w-5 text-white" />
              </div>
              <div className="text-left">
                <p className="font-medium">Pay with Card</p>
                <p className="text-sm text-muted-foreground">
                  Credit/Debit card via Stripe
                </p>
              </div>
            </Button>
          </CardContent>
          <CardFooter>
            <p className="text-xs text-muted-foreground">
              Payment is processed securely. Your subscription will be activated immediately after successful payment.
            </p>
          </CardFooter>
        </Card>
      </div>

      {payments && payments.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Payment History</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {payments.map((payment) => (
                <div key={payment.id} className="flex items-center justify-between rounded-lg border p-4">
                  <div className="flex items-center gap-3">
                    <div className={`flex h-10 w-10 items-center justify-center rounded-full ${
                      payment.status === "completed" ? "bg-primary/10" : "bg-muted"
                    }`}>
                      {payment.paymentMethod === "gcash" ? (
                        <Smartphone className="h-5 w-5" />
                      ) : (
                        <CreditCard className="h-5 w-5" />
                      )}
                    </div>
                    <div>
                      <p className="font-medium capitalize">{payment.paymentMethod} Payment</p>
                      <p className="text-sm text-muted-foreground">
                        {payment.paymentDate ? formatDate(payment.paymentDate) : "Processing"}
                      </p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="font-mono font-medium">{formatPHP(payment.amountPaid)}</p>
                    <Badge variant={payment.status === "completed" ? "default" : "secondary"}>
                      {payment.status}
                    </Badge>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

